import React, { useState } from "react";

import './Auth.css'
import icon from '../../assets/icon.svg'

const Auth = () => {

  const [isSignup, setIsSignup] = useState(false);

  const handleSwitch = () => {
    setIsSignup(!isSignup);
  };

  return (
    <section className="auth-section">
      <div className="auth-container">
        { isSignup && <img src={icon} alt='Stack-overflow' className="login-logo"/>}
        <form>
          {
            isSignup && (
              <label htmlFor="name">
                <h4>Display Name</h4>
                <input type="text" id="name" name="name" />
              </label>
            )
          }
          <label htmlFor="email">
            <h4>Email</h4>
            <input type="email" name="email" id="email" />
          </label>
          <label htmlFor="password">
            <div>
              <h4>Password</h4>
              { !isSignup && <h4>forgot password?</h4>}
            </div>            
            <input type="password" name="password" id="password" />
            { isSignup && <p>Password must contain at least eight<br />characters, including at least 1 letter and 1<br />number.</p>}
          </label>
          {
            isSignup && (
              <label htmlFor="check">
                <input type="checkbox" id="check"/>
                <p>Opt-in to receive occasional product updates, user research invitations, company announcements</p>
              </label>
            )
          }
          <button type="submit" className="auth-btn">{ isSignup ? 'Sign up' : 'Log in'}</button>
        </form>
        <p>
          {isSignup ? 'Already have an account?' : "Don't have an account?"}
          <button type="button" className="handle-switch-btn" onClick={handleSwitch}>{isSignup ? 'Log in' : 'Sign up'}</button>
        </p>
      </div>
    </section>
  )
}

export default Auth
