import React from 'react'

const Avatar = ({ children, backgroundColor, px, color, borderRadius, fontSize }) => {

  const style = {
    backgroundColor,
    padding,
    color: color || 'black',
    borderRadius,
    fontSize,
    textAlign: 'center'
  }

  return (
    <div style={style}>
        { children }
    </div>
  )
}

export default Avatar
